import { Component } from '@angular/core';

@Component({
  selector: 'app-orariutente',
  templateUrl: './orariutente.component.html',
  styleUrls: ['./orariutente.component.css']
})
export class OrariutenteComponent {

}
<div class="clock">
  <div class="clock__second"></div>
  <div class="clock__minute"></div>
  <div class="clock__hour"></div>
  <div class="clock__indicator" *ngFor="let indicator of indicators"></div>
</div>
