import { Component, OnInit, Renderer2, ElementRef } from '@angular/core';

@Component({
  selector: 'app-navbarutente',
  templateUrl: './navbarutente.component.html',
  styleUrls: ['./navbarutente.component.css']
})
export class NavbarutenteComponent {
  indicators: any[] = []; // You can replace 'any[]' with a more specific type if needed

  constructor(private renderer: Renderer2, private el: ElementRef) {}

  ngOnInit(): void {
    const currentSec = this.getSecondsToday();

    const seconds = (currentSec / 60) % 1;
    const minutes = (currentSec / 3600) % 1;
    const hours = (currentSec / 43200) % 1;

    this.setTime(60 * seconds, 'second');
    this.setTime(3600 * minutes, 'minute');
    this.setTime(43200 * hours, 'hour');
  }

  setTime(left: number, hand: string): void {
    const clockHand = this.renderer.selectRootElement(`.clock__${hand}`, true);

    // Replace the following line with Angular-specific logic to set animation-delay
    // For simplicity, I'm using a console.log statement.
    console.log(`Setting animation-delay for .clock__${hand}: ${left * -1}s`);

    // If you have an array of indicators to loop through, you can adapt the following logic
    this.indicators.forEach((indicator, index) => {
      const indicatorElement = this.renderer.selectRootElement(`.clock__indicator:nth-of-type(${index + 1})`, true);
      // Set animation delay for indicators
      // Replace the following line with Angular-specific logic to set animation-delay
      console.log(`Setting animation-delay for .clock__indicator(${index + 1}): ${left * -1}s`);
    });
  }

  getSecondsToday(): number {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());

    const diff = now.getTime() - today.getTime();
    return Math.round(diff / 1000);
  }
}
