import { Component } from '@angular/core';

interface Persona {
  nome: string;
  cognome: string;
  entra: boolean;
  esci: boolean;
}

@Component({
  selector: 'app-orariutente',
  templateUrl: './orariutente.component.html',
  styleUrls: ['./orariutente.component.css']
})
export class OrariutenteComponent {

  public persone: Persona[] = [
    { nome: 'Mark', cognome: 'Otto', entra: true, esci: false },
    { nome: 'Jacob', cognome: 'Thornton', entra: true, esci: false },
    { nome: 'Larry', cognome: 'Bird', entra: true, esci: false }
  ];

  toggleButtons(index: number): void {
    this.persone[index].entra = !this.persone[index].entra;
    this.persone[index].esci = !this.persone[index].esci;
  }
}
