import { Component } from '@angular/core';

@Component({
  selector: 'app-orariutente',
  templateUrl: './orariutente.component.html',
  styleUrls: ['./orariutente.component.css']
})
export class OrariutenteComponent {
  indicators: number[] = Array.from({ length: 60 }, (_, index) => index + 1);

  ngOnInit(): void {
    this.setupClock();
  }

  setupClock() {
    const currentSec = this.getSecondsToday();
    const seconds = (currentSec / 60) % 1;
    const minutes = (currentSec / 3600) % 1;
    const hours = (currentSec / 43200) % 1;

    this.setTime(60 * seconds, 'second');
    this.setTime(3600 * minutes, 'minute');
    this.setTime(43200 * hours, 'hour');
  }

  setTime(left: number, hand: string) {
    // Implement the logic to set animation delay for each hand
  }

  getSecondsToday(): number {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    const diff = now.getTime() - today.getTime();
    return Math.round(diff / 1000);
  }
}
<div class="clock">
  <div class="clock__second"></div>
  <div class="clock__minute"></div>
  <div class="clock__hour"></div>
  <div class="clock__indicator" *ngFor="let indicator of indicators"></div>
</div>
