import { Component } from '@angular/core';

@Component({
  selector: 'app-homeutente',
  templateUrl: './homeutente.component.html',
  styleUrls: ['./homeutente.component.css']
})
export class HomeutenteComponent {
  isIngEnabled = true;
  isExitEnabled = false;

  toggleButtons() {
    this.isIngEnabled = !this.isIngEnabled;
    this.isExitEnabled = !this.isExitEnabled;
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no">
  <title>Welcome Admin</title>
  <!-- Bootstrap CSS -->
  <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css">
  <!-- Custom CSS -->
  <style>
    body {
      background-color: #f8f9fa;
    }

    .welcome-container {
      margin-top: 50px;
    }

    .welcome-text {
      font-size: 24px;
      font-weight: bold;
      margin-bottom: 20px;
    }

    .admin-icon {
      font-size: 48px;
      color: #007bff;
    }
  </style>
</head>

<body>
  <div class="container welcome-container text-center">
    <i class="fas fa-user-shield admin-icon"></i>
    <div class="welcome-text">Welcome to work!</div>
    <p class="lead">Ready to go to work?</p>
  </div>

  <div class="container welcome-container text-center">
    <i class="fas fa-user-shield admin-icon"></i>
    <button id="ing" [disabled]="!isIngEnabled" (click)="toggleButtons()">Enter</button>
    <button id="exit" [disabled]="!isExitEnabled" (click)="toggleButtons()">Exit</button>
  </div>

  <!-- Bootstrap JS and Popper.js -->
  <script src="https://code.jquery.com/jquery-3.5.1.slim.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.2/dist/umd/popper.min.js"></script>
  <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
  <!-- Font Awesome -->
  <script src="https://kit.fontawesome.com/your-fontawesome-kit.js" crossorigin="anonymous"></script>
</body>

</html>
